import { useState, useEffect } from "react"
const Record = (props) => (
    <tr>
        <td>{props.record.name}</td>
        <td>{props.record.email}</td>
        <td>{props.record.phoneNumber}</td>
        <td>{props.record.location}</td>
        <td>
            <Link className="btn btn-link" to={`/edit/${props.record._id}`}>Edit</Link> |
            <button className="btn btn-link" onClick={() => { props.deleteRecord(props.record._id); }}>
                Delete
            </button>
        </td>
    </tr>
);
const Vendor = () => {
    const [customers, setCustomers] = useState([]);

    // This method fetches the records from the database.
    useEffect(() => {
        async function getCustomers() {
            const response = await fetch(`http://localhost:5000/customer/`);

            if (!response.ok) {
                const message = `An error occurred: ${response.statusText}`;
                window.alert(message);
                return;
            }
            const customers = await response.json();
            setCustomers(customers);
        }
        getCustomers();

        return;
    }, [customers.length]);

    // This method will delete a record
    async function deleteCustomer(id) {
        await fetch(`http://localhost:5000/${id}`, {
            method: "DELETE"
        });

        const newCustomers = customers.filter((el) => el._id !== id);
        setCustomers(newCustomers);
    }

        // This method will map out the records on the table
        function customerList() {
            return customers.map((customer) => {
                return (
                    <Customer
                        customer={customer}
                        deleteCustomer = {() => deleteCustomer(customer._id)}
                        key={customer._id}
                    />
                );
            });
        }

return (
    <div>
        <h3>Customers List</h3>
        <table className="table table-striped" style={{ marginTop: 20 }}>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Phone Number</th>
                    <th>Location</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>{ customerList()}</tbody>
        </table>
    </div>
);
}
export default Vendor
