import React,{useState} from 'react'
import { Grid, Paper, Typography, TextField, Button } from '@material-ui/core'
import { useNavigate } from "react-router";

const Customer = () => {
    const paperStyle = { padding: '30px 20px', height: '500px', width: '500px', margin: "20px auto" }
    const headerStyle = { margin: 0 }
    const marginTop = { marginTop: 15 }
    const [form, setForm] = useState({
        name: "",
        email: "",
        phoneNumber: "",
        location: ""
        
        });
    const navigate = useNavigate();

    // These methods will update the state properties.
    function updateForm(value) {
        return setForm((prev) => {
            return { ...prev, ...value };
        });
    }

    // This function will handle the submission.
    async function submitDetails(e) {
        e.preventDefault();

        // When a post request is sent to the create url, we'll add a new record to the database.
        const newCustomer = { ...form };

        await fetch("http://localhost:5000/customers/", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(newCustomer),
           
        })
        .catch(error => {
            window.alert(error);
            return;
        });
        setForm({ name: "", email: "", phoneNumber: "", location:""});
        navigate("/");
    }

    return (
        <Grid>
            <Paper elevation={20} style={paperStyle}>
                <Grid align='center'>
                <h2 style={headerStyle}>Customer Details</h2>
                    <Typography variant='caption' gutterBottom>Please fill in the details below.</Typography>
                </Grid>
                <form>
                    <TextField id="name" value={form.name} onChange={(e) => updateForm({ name: e.target.value })}
                        fullWidth label='Name' placeholder="Enter your name" />
                    <TextField id="email" value={form.email} onChange={(e) => updateForm({ email: e.target.value })}
                        fullWidth label='Email' placeholder="Enter your email" />
                    <TextField id="phoneNumber" value={form.phoneNumber} onChange={(e) => updateForm({ phoneNumber: e.target.value })}
                        fullWidth label='Phone Number' placeholder="Enter your phone number" />
                    <TextField id="location" value={form.location} onChange={(e) => updateForm({ location: e.target.value })}
                        fullWidth label='Location' placeholder="Apartment,City" />
                    <Button type='submit' variant='contained' color='primary' style={marginTop} onClick={() => submitDetails()}>Submit</Button>
                </form>
            </Paper>
        </Grid>
    )
}

export default Customer;
