import React from 'react'
import './App.css'
import { Grid, Paper, Typography, TextField, Button } from '@material-ui/core'
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
const handleChange = (event) => {
    setAge(event.target.value);
};

const App = () => {
  const paperStyle = {
    padding: '30px 20px', height: '500px', width: '700px', margin: "20px auto"
  }
  const headerStyle = { margin: 0 }
  const marginTop = { marginTop: 5 }
  return (
    <div className="App">
    <Grid>
      <Paper elevation={20} style={paperStyle}>
        <Grid align='center'>
          <h2 style={headerStyle}>Customer Details</h2>
          <Typography variant='caption' gutterBottom>Please fill in the details below.</Typography>
        </Grid>
        <form>
          <TextField fullWidth label='Name' placeholder="Enter your name" />
          <TextField fullWidth label='Email' placeholder="Enter your email" />
          <TextField fullWidth label='Phone Number' placeholder="Enter your phone number" />
            <TextField fullWidth label='Location' placeholder="Address" />
            <FormControl fullWidth>
              <InputLabel id="demo-simple-select-label">Age</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={age}
                label="Age"
                onChange={handleChange}
              >
                <MenuItem value={10}>Ten</MenuItem>
                <MenuItem value={20}>Twenty</MenuItem>
                <MenuItem value={30}>Thirty</MenuItem>
              </Select>
            </FormControl>

          <Button type='submit' variant='contained' color='primary'>Submit</Button>
        </form>
      </Paper>
      </Grid>
    </div>
  )
}

export default App;
