import React,{useState} from 'react'
import { Grid, Paper, Typography, TextField, Button } from '@material-ui/core'
import axios from "axios";

export default function Customer() {
    const paperStyle = { padding: '30px 20px', height: '500px', width: '500px', margin: "20px auto" }
    const headerStyle = { margin: 0 }
    const marginTop = { marginTop: 15 }
    const [customer, setCustomer] = useState({
        name: 0,
        email: "",
        phoneNumber: "",
        location:"",
        subscribedToPackage: "",
    });
    const createCustomer = () => {
        axios.post("http://localhost:5000/customers", customer).then(() => {
            window.location.reload(false);
        })
    };


    return (
        <Grid>
            <Paper elevation={20} style={paperStyle}>
                <Grid align='center'>
                <h2 style={headerStyle}>Customer Details</h2>
                    <Typography variant='caption' gutterBottom>Please fill in the details below.</Typography>
                </Grid>
                <form>
                    <TextField id="name" value={customer.name} onChange={(e) => setCustomer({...customer, name: e.target.value })}
                        fullWidth label='Name' placeholder="Enter your name" />
                    <TextField id="email" value={customer.email} onChange={(e) => setCustomer({ ...customer,email: e.target.value })}
                        fullWidth label='Email' placeholder="Enter your email" />
                    <TextField id="phoneNumber" value={customer.phoneNumber} onChange={(e) => setCustomer({ ...customer,phoneNumber: e.target.value })}
                        fullWidth label='Phone Number' placeholder="Enter your phone number" />
                    <TextField id="location" value={customer.location} onChange={(e) => setCustomer({...customer, location: e.target.value })}
                        fullWidth label='Location' placeholder="Apartment,City" />
                    <TextField id= "Subscription" value={customer.subscribedToPackage} onChange={(e) => setCustomer({ ...customer, subscribedToPackage: e.target.value })}
                        fullWidth label='Location' placeholder="Apartment,City" />
                    <Button type='submit' variant='contained' color='primary' style={marginTop} onClick={createCustomer}>Submit</Button>
                </form>
            </Paper>
        </Grid>
    )
}
